export const namespaced = true;

export const state = {
  show: false,
  message: '',
  type: 'success', // Có thể mở rộng sau này (error, warning...)
};

export const mutations = {
  // Bật popup với một thông điệp
  SHOW_NOTIFICATION(state, { message, type }) {
    state.show = true;
    state.message = message;
    state.type = type || 'success';
  },
  // Tắt popup
  HIDE_NOTIFICATION(state) {
    state.show = false;
  },
};

export const actions = {
  // Action để hiển thị thông báo và tự động ẩn sau 2.5 giây
  showNotification({ commit }, { message, type }) {
    commit('SHOW_NOTIFICATION', { message, type });

    // Tự động ẩn thông báo
    setTimeout(() => {
      commit('HIDE_NOTIFICATION');
    }, 2500);
  },
};
